import { NextRequest, NextResponse } from 'next/server';

// API Mapbox Geocoding pour recherche intelligente
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const query = searchParams.get('q');
    const limit = searchParams.get('limit') || '10';
    const country = searchParams.get('country') || 'MQ'; // Martinique
    const types = searchParams.get('types') || 'place,locality,neighborhood,address,poi';

    if (!query || query.length < 2) {
      return NextResponse.json({
        success: false,
        error: 'Requ√™te trop courte (minimum 2 caract√®res)'
      }, { status: 400 });
    }

    const mapboxToken = process.env.NEXT_PUBLIC_MAPBOX_TOKEN;
    if (!mapboxToken) {
      throw new Error('Token Mapbox manquant');
    }

    // Construire l'URL Mapbox Geocoding - Recherche mondiale
    const mapboxUrl = new URL('https://api.mapbox.com/geocoding/v5/mapbox.places');
    mapboxUrl.pathname += `/${encodeURIComponent(query)}.json`;
    mapboxUrl.searchParams.set('access_token', mapboxToken);
    mapboxUrl.searchParams.set('types', types);
    mapboxUrl.searchParams.set('limit', limit);
    mapboxUrl.searchParams.set('language', 'fr');
    mapboxUrl.searchParams.set('autocomplete', 'true');
    mapboxUrl.searchParams.set('fuzzyMatch', 'true');

    const response = await fetch(mapboxUrl.toString());

    if (!response.ok) {
      throw new Error(`Erreur Mapbox: ${response.status}`);
    }

    const data = await response.json();

    // Transformer les r√©sultats Mapbox en format standardis√©
    const results = data.features.map((feature: unknown) => {
      // D√©terminer le type bas√© sur les propri√©t√©s Mapbox
      let itemType = 'lieu';
      const placeType = feature.place_type?.[0];

      if (placeType === 'place' && feature.properties?.category === 'place') {
        itemType = 'commune';
      } else if (placeType === 'locality') {
        itemType = 'quartier';
      } else if (placeType === 'address') {
        itemType = 'rue';
      } else if (placeType === 'poi') {
        itemType = 'lieu';
      } else if (placeType === 'neighborhood') {
        itemType = 'quartier';
      }

      // Extraire les informations d'adresse
      const context = feature.context || [];
      const city = context.find((c: unknown) => c.id.startsWith('place.'))?.text || 
                   context.find((c: unknown) => c.id.startsWith('locality.'))?.text;
      const region = context.find((c: unknown) => c.id.startsWith('region.'))?.text;
      const country = context.find((c: unknown) => c.id.startsWith('country.'))?.text;

      return {
        id: feature.id,
        name: feature.text,
        type: itemType,
        coordinates: feature.center,
        address: feature.place_name,
        // Informations structur√©es
        address_details: {
          city: city,
          region: region,
          country: country,
          postcode: feature.properties?.address
        },
      // Propri√©t√©s Mapbox
      relevance: feature.relevance,
      category: feature.properties?.category,
      maki: feature.properties?.maki, // Ic√¥ne
      // Informations additionnelles
      bbox: feature.bbox,
      place_type: feature.place_type,
      // Score de pertinence
      score: feature.relevance * 100,
      // Indicateur de localisation
      is_martinique: feature.context?.some((c: unknown) => c.id.startsWith('country.') && c.text === 'Martinique') || false
      };
    });

    return NextResponse.json({
      success: true,
      results: results,
      total: results.length,
      query: query,
      attribution: '¬© Mapbox ¬© OpenStreetMap',
      // M√©tadonn√©es
      metadata: {
        source: 'mapbox',
        search_scope: 'worldwide',
        types: types,
        language: 'fr'
      }
    });

  } catch (error) {
    console.error('Erreur geocoding:', error);
    return NextResponse.json({
      success: false,
      error: 'Erreur lors de la recherche g√©ographique',
      details: error instanceof Error ? error.message : 'Erreur inconnue'
    }, { status: 500 });
  }
}

// API pour suggestions rapides (autocomplete)
export async function POST(request: NextRequest) {
  try {
    const { query, limit = 5 } = await request.json();

    if (!query || query.length < 2) {
      return NextResponse.json({
        success: false,
        error: 'Requ√™te trop courte'
      }, { status: 400 });
    }

    const mapboxToken = process.env.NEXT_PUBLIC_MAPBOX_TOKEN;
    if (!mapboxToken) {
      throw new Error('Token Mapbox manquant');
    }

    // Recherche rapide avec autocomplete
    const mapboxUrl = new URL('https://api.mapbox.com/geocoding/v5/mapbox.places');
    mapboxUrl.pathname += `/${encodeURIComponent(query)}.json`;
    mapboxUrl.searchParams.set('access_token', mapboxToken);
    mapboxUrl.searchParams.set('country', 'MQ');
    mapboxUrl.searchParams.set('types', 'place,locality,neighborhood,address');
    mapboxUrl.searchParams.set('limit', limit.toString());
    mapboxUrl.searchParams.set('language', 'fr');
    mapboxUrl.searchParams.set('autocomplete', 'true');
    mapboxUrl.searchParams.set('fuzzyMatch', 'true');

    const response = await fetch(mapboxUrl.toString());

    if (!response.ok) {
      throw new Error(`Erreur Mapbox: ${response.status}`);
    }

    const data = await response.json();

    const suggestions = data.features.map((feature: unknown) => ({
      id: feature.id,
      name: feature.text,
      type: feature.place_type?.[0] || 'lieu',
      coordinates: feature.center,
      address: feature.place_name,
      relevance: feature.relevance,
      score: feature.relevance * 100
    }));

    return NextResponse.json({
      success: true,
      suggestions: suggestions
    });

  } catch (error) {
    console.error('Erreur suggestions:', error);
    return NextResponse.json({
      success: false,
      error: 'Erreur lors de la r√©cup√©ration des suggestions'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';

// API pour rechercher des donn√©es r√©elles via OpenStreetMap Nominatim
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const query = searchParams.get('q');
    const type = searchParams.get('type') || 'all';
    const limit = searchParams.get('limit') || '10';

    if (!query || query.length < 2) {
      return NextResponse.json({
        success: false,
        error: 'Requ√™te trop courte (minimum 2 caract√®res)'
      }, { status: 400 });
    }

    // Construire la requ√™te Nominatim pour la Martinique
    const nominatimUrl = new URL('https://nominatim.openstreetmap.org/search');
    nominatimUrl.searchParams.set('q', `${query}, Martinique, France`);
    nominatimUrl.searchParams.set('format', 'json');
    nominatimUrl.searchParams.set('limit', limit);
    nominatimUrl.searchParams.set('addressdetails', '1');
    nominatimUrl.searchParams.set('extratags', '1');
    nominatimUrl.searchParams.set('namedetails', '1');
    nominatimUrl.searchParams.set('bounded', '1');
    nominatimUrl.searchParams.set('viewbox', '-61.2,14.3,-60.8,14.9'); // Bounding box Martinique
    nominatimUrl.searchParams.set('countrycodes', 'mq');

    const response = await fetch(nominatimUrl.toString(), {
      headers: {
        'User-Agent': 'Padavwa-Urban-Analysis/1.0'
      }
    });

    if (!response.ok) {
      throw new Error(`Erreur Nominatim: ${response.status}`);
    }

    const data = await response.json();

    // Transformer les donn√©es Nominatim en format standardis√©
    const results = data.map((item: unknown) => {
      // D√©terminer le type bas√© sur les donn√©es Nominatim
      let itemType = 'lieu';
      if (item.type === 'administrative' && item.address?.city) {
        itemType = 'commune';
      } else if (item.type === 'residential' || item.address?.suburb) {
        itemType = 'quartier';
      } else if (item.type === 'highway' || item.address?.road) {
        itemType = 'rue';
      } else if (item.type === 'tourism' || item.type === 'amenity') {
        itemType = 'lieu';
      }

      return {
        id: item.place_id.toString(),
        name: item.display_name.split(',')[0], // Premier √©l√©ment du nom
        type: itemType,
        coordinates: [parseFloat(item.lon), parseFloat(item.lat)],
        address: item.display_name,
        osm_type: item.type,
        importance: item.importance,
        class: item.class,
        // Informations additionnelles
        population: item.extratags?.population ? parseInt(item.extratags.population) : undefined,
        area: item.extratags?.area ? parseFloat(item.extratags.area) : undefined,
        website: item.extratags?.website,
        phone: item.extratags?.phone,
        opening_hours: item.extratags?.['opening_hours'],
        // Adresse structur√©e
        address_details: {
          city: item.address?.city || item.address?.town || item.address?.village,
          state: item.address?.state,
          country: item.address?.country,
          postcode: item.address?.postcode
        }
      };
    });

    // Filtrer par type si sp√©cifi√©
    const filteredResults = type === 'all' 
      ? results 
      : results.filter(item => item.type === type);

    return NextResponse.json({
      success: true,
      results: filteredResults,
      total: filteredResults.length,
      query: query,
      type: type
    });

  } catch (error) {
    console.error('Erreur recherche:', error);
    return NextResponse.json({
      success: false,
      error: 'Erreur lors de la recherche',
      details: error instanceof Error ? error.message : 'Erreur inconnue'
    }, { status: 500 });
  }
}

// API pour r√©cup√©rer des suggestions de recherche
export async function POST(request: NextRequest) {
  try {
    const { query, type } = await request.json();

    if (!query || query.length < 2) {
      return NextResponse.json({
        success: false,
        error: 'Requ√™te trop courte'
      }, { status: 400 });
    }

    // Recherche avec des suggestions plus larges
    const nominatimUrl = new URL('https://nominatim.openstreetmap.org/search');
    nominatimUrl.searchParams.set('q', `${query}, Martinique`);
    nominatimUrl.searchParams.set('format', 'json');
    nominatimUrl.searchParams.set('limit', '5');
    nominatimUrl.searchParams.set('addressdetails', '1');
    nominatimUrl.searchParams.set('extratags', '1');
    nominatimUrl.searchParams.set('bounded', '1');
    nominatimUrl.searchParams.set('viewbox', '-61.2,14.3,-60.8,14.9');
    nominatimUrl.searchParams.set('countrycodes', 'mq');

    const response = await fetch(nominatimUrl.toString(), {
      headers: {
        'User-Agent': 'Padavwa-Urban-Analysis/1.0'
      }
    });

    if (!response.ok) {
      throw new Error(`Erreur Nominatim: ${response.status}`);
    }

    const data = await response.json();

    const suggestions = data.map((item: unknown) => ({
      id: item.place_id.toString(),
      name: item.display_name.split(',')[0],
      type: item.type,
      coordinates: [parseFloat(item.lon), parseFloat(item.lat)],
      address: item.display_name
    }));

    return NextResponse.json({
      success: true,
      suggestions: suggestions
    });

  } catch (error) {
    console.error('Erreur suggestions:', error);
    return NextResponse.json({
      success: false,
      error: 'Erreur lors de la r√©cup√©ration des suggestions'
    }, { status: 500 });
  }
}

