"use client";

import { useEffect, useRef, useState } from 'react';

interface Point {
  id: string;
  lng: number;
  lat: number;
  label: string;
}

export default function ForceDrawingMap() {
  const mapContainer = useRef<HTMLDivElement>(null);
  const map = useRef<unknown>(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [points, setPoints] = useState<Point[]>([]);
  const [debugInfo, setDebugInfo] = useState<string>('Initialisation...');

  useEffect(() => {
    if (!mapContainer.current) return;

    setDebugInfo('Chargement de Mapbox...');

    import('mapbox-gl').then((mapbox) => {
      if (!mapContainer.current) return;

      try {
        const mapInstance = new mapbox.Map({
          container: mapContainer.current,
          style: 'mapbox://styles/mapbox/streets-v12',
          center: [2.3522, 48.8566], // Paris
          zoom: 13,
          accessToken: process.env.NEXT_PUBLIC_MAPBOX_TOKEN || ''
        });

        mapInstance.addControl(new mapbox.NavigationControl(), 'top-right');

        mapInstance.on('load', () => {
          setDebugInfo('‚úÖ Carte charg√©e et pr√™te');
          setIsLoaded(true);
        });

        mapInstance.on('click', (e) => {
          if (!isLoaded) return;

          const pointId = `point-${Date.now()}`;
          const newPoint: Point = {
            id: pointId,
            lng: e.lngLat.lng,
            lat: e.lngLat.lat,
            label: `Point ${points.length + 1}`
          };

          setPoints(prev => [...prev, newPoint]);
          setDebugInfo(`Point ajout√©: ${newPoint.label}`);

          // Cr√©er un marqueur
          const marker = new mapbox.Marker()
            .setLngLat([e.lngLat.lng, e.lngLat.lat])
            .setPopup(
              new mapbox.Popup().setText(newPoint.label)
            )
            .addTo(mapInstance);

          marker.getElement().addEventListener('click', () => {
            marker.togglePopup();
          });
        });

        mapInstance.on('error', (e) => {
          console.error('Erreur Mapbox:', e);
          setError(`Erreur de carte: ${e.error?.message || 'Erreur inconnue'}`);
          setDebugInfo('‚ùå Erreur lors du chargement');
        });

        map.current = mapInstance;

        return () => {
          if (map.current) {
            (map.current as any).remove();
          }
        };
      } catch (err) {
        console.error('Erreur lors de l\'initialisation:', err);
        setError(`Erreur d'initialisation: ${err instanceof Error ? err.message : 'Erreur inconnue'}`);
        setDebugInfo('‚ùå Erreur lors de l\'initialisation');
      }
    }).catch((err) => {
      console.error('Erreur lors du chargement de Mapbox:', err);
      setError(`Erreur de chargement: ${err.message}`);
      setDebugInfo('‚ùå Erreur lors du chargement');
    });
  }, [points.length]);

  const clearAllPoints = () => {
    console.log('üóëÔ∏è Suppression de tous les points...');
    setDebugInfo('Suppression de tous les points...');

    if (map.current) {
      try {
        // Supprimer tous les marqueurs de la carte
        document.querySelectorAll('.mapboxgl-marker').forEach(marker => marker.remove());
        setPoints([]);
        setDebugInfo('‚úÖ Tous les points supprim√©s');
      } catch (err) {
        console.error('‚ùå Erreur lors de la suppression:', err);
        setDebugInfo('‚ùå Erreur lors de la suppression');
      }
    }
  };

  const removePoint = (pointId: string) => {
    setPoints(prev => prev.filter(p => p.id !== pointId));
    setDebugInfo(`Point supprim√©`);
  };

  if (error) {
    return (
      <div className="h-96 bg-gradient-to-br from-red-50 to-orange-50 rounded-lg border border-red-200 flex items-center justify-center">
        <div className="text-center p-6">
          <div className="text-4xl mb-3">‚ùå</div>
          <h3 className="text-lg font-semibold text-red-700 mb-2">Erreur de carte</h3>
          <p className="text-red-600 text-sm">{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Instructions simples */}
      <div className="bg-gradient-to-r from-blue-50 to-green-50 p-4 rounded-lg border border-blue-200">
        <h3 className="font-semibold text-blue-800 mb-3 flex items-center">
          üìç Mode Points Bleus - Instructions
        </h3>
        <div className="space-y-2 text-sm text-blue-700">
          <p><strong>√âtape 1:</strong> Attendez que &quot;‚úÖ Carte charg√©e et pr√™te&quot; apparaisse</p>
          <p><strong>√âtape 2:</strong> Cliquez directement sur la carte pour ajouter des points bleus</p>
          <p><strong>√âtape 3:</strong> Chaque clic ajoute un marqueur bleu avec popup</p>
          <p><strong>√âtape 4:</strong> Utilisez les boutons pour g√©rer vos points</p>
        </div>
      </div>

      {/* Debug info */}
