  onClose,
  score,
  totalQuestions,
  pointsEarned,
  reward
}: ResultModalProps) {
  const percentage = Math.round((score / totalQuestions) * 100);
  const isExcellent = percentage >= 80;
  const isGood = percentage >= 60;

  useEffect(() => {
    if (isOpen) {
      // Animation de confetti pour les bons scores
      if (percentage >= 70) {
        confetti({
          particleCount: 150,
          spread: 70,
          origin: { y: 0.6 }
