  { type: 'points' as const, amount: 100, label: '+100 points', description: 'Excellent score ! Vous gagnez des points bonus !' },
  { type: 'coupon' as const, label: 'R√©duction 10%', description: 'Coupon de r√©duction valable chez nos partenaires !' },
  { type: 'points' as const, amount: 25, label: '+25 points', description: 'Merci d\'avoir particip√© au quiz !' }
];

export default function ScratchFlow() {
  const [step, setStep] = useState<"video" | "quiz" | "scratch" | "result">("video");
  const [quizScore, setQuizScore] = useState(0);
  const [totalQuestions, setTotalQuestions] = useState(0);
  const [pointsEarned, setPointsEarned] = useState(0);
  const [reward, setReward] = useState<typeof defaultRewards[0] | null>(null);

  const handleVideoEnd = () => {
    setStep("quiz");
  };

  const handleQuizComplete = (score: number, total: number) => {
    setQuizScore(score);
    setTotalQuestions(total);

    // Calculer les points gagn√©s (base + bonus selon le score)
    const basePoints = score * 10; // 10 points par bonne r√©ponse
    const bonusPoints = score === total ? 50 : 0; // Bonus si parfait
    const totalPoints = basePoints + bonusPoints;

    setPointsEarned(totalPoints);

    // S√©lectionner une r√©compense al√©atoire
    const randomReward = defaultRewards[Math.floor(Math.random() * defaultRewards.length)];
    setReward(randomReward);

    setStep("scratch");
  };

  const handleScratchReveal = () => {
    setStep("result");
  };

  const handleRestart = () => {
    setStep("video");
    setQuizScore(0);
    setTotalQuestions(0);
    setPointsEarned(0);
    setReward(null);
  };

  return (
    <>
      {step === "video" && (
        <VideoModal onEnd={handleVideoEnd} />
      )}

      {step === "quiz" && (
        <Quiz 
          questions={defaultQuestions}
          onComplete={handleQuizComplete}
          timeLimit={120} // 2 minutes
        />
      )}

      {step === "scratch" && (
        <ScratchCard 
          reward={reward || defaultRewards[0]}
          onReveal={handleScratchReveal}
          width={400}
          height={200}
          coverText="Grattez pour r√©v√©ler votre r√©compense !"
        />
      )}

      {step === "result" && (
        <ResultModal 
          isOpen={true}
          onClose={handleRestart}
          score={quizScore}
          totalQuestions={totalQuestions}
          pointsEarned={pointsEarned}
          reward={reward}
        />
      )}
    </>
  );
}

