"use client";

import { useState, useEffect } from 'react';

interface SimulatedMapProps {
  className?: string;
  height?: string;
  center?: [number, number];
  zoom?: number;
}

export default function SimulatedMap({ 
  className = "", 
  height = "400px",
  center = [2.3522, 48.8566], // Paris par d√©faut
  zoom = 12
}: SimulatedMapProps) {
  const [isLoaded, setIsLoaded] = useState(false);
  const [mousePosition, setMousePosition] = useState<{ x: number; y: number } | null>(null);


const stableSetTimeout = useCallback(() => {
  setTimeout();
}, [setTimeout]);

const stableSetIsLoaded = useCallback(() => {
  setIsLoaded();
}, [setIsLoaded]);

const stableClearTimeout = useCallback(() => {
  clearTimeout();
}, [clearTimeout]);

useEffect(() => {
  stableSetTimeout();
  stableSetIsLoaded();
  stableClearTimeout();
}, [stableSetTimeout, stableSetIsLoaded, stableClearTimeout]);;

  const handleMouseMove = (e: React.MouseEvent) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setMousePosition({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
  };

  const handleMouseLeave = () => {
    setMousePosition(null);
  };

  return (
    <div className={`relative ${className}`} style={{ height }}>
      {/* Carte simul√©e avec OpenStreetMap */}
      <div 
        className="w-full h-full rounded-lg overflow-hidden relative"
        style={{ height }}
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
      >
        <iframe
          src={`https://www.openstreetmap.org/export/embed.html?bbox=${center[0]-0.01},${center[1]-0.01},${center[0]+0.01},${center[1]+0.01}&layer=mapnik&marker=${center[1]},${center[0]}`}
          className="w-full h-full border-0"
          title="Carte OpenStreetMap"
        />

        {/* Overlay de simulation Mapbox */}
        <div className="absolute inset-0 pointer-events-none">
          {/* Contr√¥les simul√©s */}
          <div className="absolute top-4 right-4 flex flex-col gap-2">
