export default function QuizModal() {
  const { close } = useModal();
  const [selected, setSelected] = useState<string | null>(null);
  const [validated, setValidated] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  const questions = [
    {
      id: "q1",
      text: "Quelle est une bonne source de prot√©ines v√©g√©tales ?",
      options: [
        { id: "banane", text: "Banane" },
        { id: "concombre", text: "Concombre" },
        { id: "lentilles", text: "Lentilles" }, // ‚úÖ bonne r√©ponse
        { id: "pdt", text: "Pomme de terre" },
      ],
      correct: "lentilles",
      points: 10
    },
    {
      id: "q2", 
      text: "Quel est le code couleur principal de Kanpanya ?",
      options: [
        { id: "red", text: "#FF0000" },
        { id: "teal", text: "#14B8A6" }, // ‚úÖ bonne r√©ponse
        { id: "blue", text: "#3B82F6" },
        { id: "green", text: "#10B981" },
      ],
      correct: "teal",
      points: 10
    },
    {
      id: "q3",
      text: "Combien de points gagnez-vous en regardant une vid√©o ?",
      options: [
        { id: "3", text: "3 points" },
        { id: "5", text: "5 points" }, // ‚úÖ bonne r√©ponse
        { id: "10", text: "10 points" },
        { id: "15", text: "15 points" },
      ],
      correct: "5",
      points: 10
    }
  ];

  const currentQ = questions[currentQuestion];
  const isLastQuestion = currentQuestion === questions.length - 1;

  const handleValidate = () => {
    if (selected === currentQ.correct) {
      setScore(prev => prev + currentQ.points);
    }
    setValidated(true);
  };

  const handleNext = () => {
    if (isLastQuestion) {
      // Quiz termin√©
      console.log("Quiz termin√©:", { score: score > 0 ? 1 : 0, total: questions.length, points: score });
      close();
    } else {
      // Passer √† la question suivante
      setCurrentQuestion(prev => prev + 1);
      setSelected(null);
      setValidated(false);
    }
  };

  const handleClose = () => {
    close();
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h2 className="text-teal-600 font-bold text-lg">Quiz Nutrition</h2>
        <span className="text-sm text-gray-500">
          Question {currentQuestion + 1} / {questions.length}
        </span>
      </div>

      {/* Question */}
      <motion.p 
        key={currentQuestion}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center font-semibold text-lg"
      >
        {currentQ.text}
      </motion.p>

      {/* Options */}
      <div className="space-y-3">
        {currentQ.options.map((option, index) => (
          <QuizOption
            key={option.id}
            text={option.text}
            onClick={() => !validated && setSelected(option.id)}
            status={
              !validated ? "default" :
              option.id === currentQ.correct ? "correct" :
              option.id === selected ? "incorrect" : "default"
            }
          />
        ))}
      </div>

      {/* Actions */}
      <div className="flex gap-3 justify-end">
        <button
          onClick={handleClose}
          className="btn btn-ghost"
        >
          Fermer
        </button>
        {!validated ? (
          <button
            onClick={handleValidate}
            disabled={!selected}
            className="btn btn-primary"
          >
            Valider
          </button>
        ) : (
          <button
            onClick={handleNext}
            className="btn btn-primary"
          >
            {isLastQuestion ? "Terminer" : "Suivant"}
          </button>
        )}
      </div>
    </div>
  );
}

