  return (
    <div className="flex flex-col items-center justify-center p-6 relative">
      <h2 className="text-2xl font-extrabold mb-4 text-center animate-bounce">
        üéüÔ∏è‚ú® Gratte ton ticket magique ‚ú®üéüÔ∏è
